function r(e,o){document.querySelector(":root").style.setProperty(e,o)}document.addEventListener("scroll",()=>{const e=document.documentElement.scrollTop,o=document.documentElement.scrollHeight,t=Math.round(100*e/o),c=Math.max(70,100-t)/100,n=document.querySelector("h1");Math.round(n.offsetTop)<=Math.round(e)&&r("--h1-scale",c)});
